+  Status Visit(const Date64Type& type) {
+    // check that data is divisible by 8.64e7 (= 1000ms * 60s * 60mins * 24hrs)
+    RETURN_NOT_OK(ValidateFixedWidthBuffers());
+    
+    using c_type = typename Date64Type::c_type;
+    if (full_validation) {
