diff --git a/cpp/src/arrow/ipc/writer.cc b/cpp/src/arrow/ipc/writer.cc
index 86f2ed1d68..b82aa9eaea 100644
--- a/cpp/src/arrow/ipc/writer.cc
+++ b/cpp/src/arrow/ipc/writer.cc
@@ -830,7 +830,9 @@ class RecordBatchStreamWriter::RecordBatchStreamWriterImpl : public StreamBookKe

     // Write 0 EOS message
     const int32_t kEos = 0;
-    return Write(&kEos, sizeof(int32_t));
+    RETURN_NOT_OK(Write(&kEos, sizeof(int32_t)));
+    // Align the stream for further data to be written to it
+    return Align();
   }

   Status CheckStarted() {
diff --git a/cpp/src/arrow/python/arrow_to_python.cc b/cpp/src/arrow/python/arrow_to_python.cc
index 3fdc5f1441..04ae9ca387 100644
--- a/cpp/src/arrow/python/arrow_to_python.cc
+++ b/cpp/src/arrow/python/arrow_to_python.cc
@@ -32,6 +32,7 @@
 #include "arrow/io/interfaces.h"
 #include "arrow/io/memory.h"
 #include "arrow/ipc/reader.h"
+#include "arrow/ipc/util.h"
 #include "arrow/table.h"
 #include "arrow/util/logging.h"

@@ -256,7 +257,10 @@ Status ReadSerializedObject(io::RandomAccessFile* src, SerializedPyObject* out)
   RETURN_NOT_OK(reader->ReadNext(&out->batch));

   RETURN_NOT_OK(src->Tell(&offset));
+
   offset += 4;  // Skip the end-of-stream message
+  offset = ipc::PaddedLength(offset, ipc::kArrowIpcAlignment);
+
   for (int i = 0; i < num_tensors; ++i) {
     std::shared_ptr<Tensor> tensor;
     RETURN_NOT_OK(ipc::ReadTensor(offset, src, &tensor));
@@ -274,6 +278,8 @@ Status ReadSerializedObject(io::RandomAccessFile* src, SerializedPyObject* out)
     out->buffers.push_back(buffer);
     RETURN_NOT_OK(src->Tell(&offset));
   }
+  offset = ipc::PaddedLength(offset, ipc::kArrowIpcAlignment);
+  (void)src->Seek(offset);

   return Status::OK();
 }
diff --git a/python/pyarrow/tests/test_serialization.py b/python/pyarrow/tests/test_serialization.py
index 16b477bc99..6646b58da5 100644
--- a/python/pyarrow/tests/test_serialization.py
+++ b/python/pyarrow/tests/test_serialization.py
@@ -714,3 +714,15 @@ def test_tensor_alignment():
     ys = pa.deserialize(pa.serialize(xs).to_buffer())
     for y in ys:
         assert y.ctypes.data % 64 == 0
+
+def test_repeated_serialization():
+    # ARROW-1996: serializing several objects to the same file
+    for u, v in zip(PRIMITIVE_OBJECTS, PRIMITIVE_OBJECTS[1:]):
+        f = pa.BufferOutputStream()
+        pa.serialize_to(u, f)
+        pa.serialize_to(v, f)
+        f = pa.BufferReader(f.get_result())
+        uu = pa.deserialize_from(f, base=None)
+        vv = pa.deserialize_from(f, base=None)
+        assert_equal(uu, u)
+        assert_equal(vv, v)




