idx = pd.date_range('2017-01-01 12:00:00.000', '2017-03-01 12:00:00.000', freq = 'T') 
dataframe = pd.DataFrame({'numeric_col' : np.random.rand(len(idx)), 
                          'string_col' : pd.util.testing.rands_array(8,len(idx))}, 
                         index = idx){code}
 
