   ```rust
   impl From<Vec<(Field, ArrayRef)>> for StructArray {
       fn from(v: Vec<(Field, ArrayRef)>) -> Self {
           let (field_types, field_values): (Vec<_>, Vec<_>) = v.into_iter().unzip();
   
           // Check the length of the child arrays
           let length = field_values[0].len();
           for i in 1..field_values.len() {
               assert_eq!(
                   length,
                   field_values[i].len(),
                   "all child arrays of a StructArray must have the same length"
               );
           }
   
           // Manipulate bitmaps to find bits that are null for whole array elements
           // If we encounter a None in the Option<Bitmap>, it means that the struct can't be
           // null as a whole
           let bitmaps: Vec<Bitmap> = field_values
               .iter()
               .map(|array| array.data().null_bitmap().clone())
               .filter_map(|map| map)
               .collect();
           let nulls_and_bitmap: (usize, Option<Buffer>) = if bitmaps.len()
               != field_values.len()
           {
               (0, None)
           } else {
               // NOTE: The below only works if offset of each array in the struct is 0,
               // otherwise the problem's that bitmaps of different lengths are compared.
   
               // Any opinions on how to compare bitmaps that have differing offsets Chao?
               let mut first_bitmap: Bitmap = bitmaps[0].clone();
               for i in 1..field_values.len() {
                   first_bitmap = first_bitmap.bitor(&bitmaps[i]).unwrap();
               }
               let valid_bits = first_bitmap.bits.data();
               // TODO if taking this approach, offsets have
               let null_count = length
                   .checked_sub(bit_util::count_set_bits_offset(valid_bits, 0, length))
                   .unwrap();
   
               (null_count, Some(first_bitmap.bits))
           };
   
           let builder = ArrayData::builder(DataType::Struct(field_types))
               .len(length)
               .null_count(nulls_and_bitmap.0)
               .child_data(field_values.into_iter().map(|a| a.data()).collect());
           let data = if let Some(buffer) = nulls_and_bitmap.1 {
               builder.null_bit_buffer(buffer).build()
           } else {
               builder.build()
           };
           Self::from(data)
       }
   }
   ```

