+  @After
+  public void shutdown() {
+    allocator.close();
+  }
+
+  @Test
+  public void testSearchInt() {
+    try (IntVector rawVector = new IntVector("", allocator)) {
+      rawVector.allocateNew(VECTOR_LENGTH);
+      rawVector.setValueCount(VECTOR_LENGTH);
