+      // prepare data in reverse order
+      for (int i = 0; i < VECTOR_LENGTH; i++) {
+        if (i == VECTOR_LENGTH - 1) {
+          rawVector.setNull(i);
+        } else {
+          rawVector.set(i, VECTOR_LENGTH - i - 1);
+        }
+      }
+
+      // sort vector
+      try (IntVector sortedVector = new IntVector("", allocator)) {
+        sortedVector.allocateNew(VECTOR_LENGTH);
+        sortedVector.setValueCount(VECTOR_LENGTH);
+
+        FixedWidthOutOfPlaceVectorSorter<IntVector> sorter = new FixedWidthOutOfPlaceVectorSorter<>();
+        VectorValueComparator<IntVector> comparator = new DefaultVectorComparators.IntComparator();
+        sorter.sortOutOfPlace(rawVector, sortedVector, comparator);
+
+        // do search
+        SortedVectorSearcher<IntVector> searcher = new SortedVectorSearcher<>(sortedVector, comparator);
+        for (int i = 0; i < VECTOR_LENGTH; i++) {
+          int result = searcher.search(rawVector, i);
+          assertEquals(VECTOR_LENGTH - 1 - i, result);
+        }
+      }
+    }
+  }
