+TYPED_TEST(TestParquetIO, EmptyColumnTable) {
+  std::shared_ptr<Array> values;
+  ASSERT_OK(NonNullArray<TypeParam>(0, &values));
+  std::shared_ptr<Table> table = MakeSimpleTable(values, false);
