-  std::random_device gen;
-  std::uniform_int_distribution<int> dist(0, static_cast<int>(chars.length() - 1));
-
-  std::string s;
-  s.reserve(num_chars);
-  for (int i = 0; i < num_chars; ++i) {
-    s += chars[dist(gen)];
-  }
-  return s;
-}
+#endif

