{{+ : BlockReader(std::move(chunker),}}
{{+ std::move(buffer_iterator),}}
{{+ std::move(first_buffer)) {}}}{{Result<arrow::util::optional<CSVBlock>> Next() {}}
{{ if (buffer_ == nullptr) {}}
